mapper.update:
UPDATE enter  SET standby = '1'
WHERE aId=#{aid}
AND eId in

    #{item, jdbcType=VARCHAR}

mapper.select:
SELECT COUNT(eId)  from enter
WHERE  eDate between   DATE_FORMAT(NOW(),'%Y-%m-%d 00:00:00')  and #{dateUp}
AND aId=#{aid}

SELECT eId,aName,eName,tIdentitycard,tQrcard,DATE_FORMAT(eDate, '%Y-%m-%d %H:%i:%s') AS eDate,CONVERT( fImage USING utf8 )  fImage  from enter
WHERE  eDate between  DATE_FORMAT(NOW(),'%Y-%m-%d 00:00:00') and #{dateUp}
AND aId=#{aid}
AND dWorker = 0

SELECT e.eId AS ID, CONCAT( e.aId, '01' ) AS performId, e.aId AS projectId, eName AS checkPass, SRAT_ROW AS seatRow, SRAT_COL AS seatCol, CHECK_CERT_TYPE AS checkCertType, BIND_CARD AS checkCertTypeNo, VANAME AS venueName, DATE_FORMAT( ENTRY_TIME, '%Y-%m-%d %H:%i:%s' ) AS entryTime, DATE_FORMAT( ENTRY_TIME, '%Y-%m-%d %H:%i:%s' ) AS checkTime, tId AS voucherId, BIND_CARD AS bindCard, BIND_NAME AS bindUserName, FLOOR_NAME AS floorName, SRAND_NAME AS standName, DEVICE_NAME AS deviceName, DEVICE_CODE AS deviceCode, DATE_FORMAT( ENTRY_TIME, '%Y-%m-%d %H:%i:%s' ) AS insertTime, BIND_CARD AS idcode, BIND_HJDXX AS hjdxx, BIND_MZXX AS mzxx FROM ( SELECT * FROM (
SELECT e.eId, e.aId, e.eName, e.tId, e.BIND_NAME, e.BIND_CARD, e.QR_CODE, e.standby, e.ENTRY_TIME, i.CHECK_CERT_TYPE, i.BIND_GENDER, i.BIND_MZXX, i.BIND_HJDXX, i.Device_CODE, i.Device_NAME FROM enter AS e LEFT JOIN enter_info AS i ON e.eId = i.eId WHERE e.aId = "HKQ0601"
) AS e LEFT JOIN ( SELECT tId AS TTID, SRAND_NAME, SRAT_ROW, SRAT_COL, FLOOR_NAME, vName AS VANAME FROM ticketing AS t LEFT JOIN activity AS a ON a.aId = t.aId ) AS t ON t.TTID = e.tId ) AS e WHERE e.standby = '0' AND e.BIND_CARD IS NOT NULL AND e.BIND_CARD != '' ORDER BY ENTRY_TIME DESC LIMIT 10

SELECT eId,aId,aName,vName,eName,tQrcard,DATE_FORMAT(eDate, '%Y-%m-%d %H:%i:%s') AS eDate,tId from enter_employee
WHERE eDate between  DATE_FORMAT(NOW(),'%Y-%m-%d 00:00:00') and #{dateUp}
AND aId=#{aid}
AND tId != 1
ORDER BY eDate DESC
LIMIT 150

SELECT COUNT(eId) from enter
WHERE eDate between #{sqlDateformerly} and #{sqlDate}
AND aId=#{aId}

SELECT e.eId FROM enter AS e WHERE e.aId=#{aId}
AND DATE(e.eDate)=#{date}
ORDER BY e.eDate DESC LIMIT 5
