mapper.select:
SELECT (
           # 票务总数
           SELECT aTicketnumber
           FROM activity
           WHERE aId = #{aId}
       ) AS aTicketnumber,

       (
           #工作证总数
           SELECT aEmployeenumber
           FROM activity
           WHERE aId = #{aId}
       ) AS aEmployeenumber,
       (
           #票务入场
           SELECT COUNT(DISTINCT e.tId)
           FROM enter AS e
           WHERE e.aId = #{aId}
             AND e.dWorker = 0
       ) AS ticket,
       (
           #工作人员入场
           SELECT COUNT(DISTINCT e.tId)
           FROM  enter AS e
           WHERE e.aId = #{aId}
             AND e.dWorker = 1
       ) AS work,

       (
           # 未实名入场
           SELECT COUNT(DISTINCT e.tId)
           FROM enter AS e
           WHERE e.aId =#{aId}
             AND (ISNULL(e.tIdentitycard) > 0 or LENGTH(e.tIdentitycard) < 1)
       ) AS noautonym,

       (
           # 实名入场
           SELECT COUNT(DISTINCT e.tId)
           FROM enter AS e
           WHERE e.aId =#{aId}
             AND (ISNULL(e.tIdentitycard) < 1 or LENGTH(e.tIdentitycard) > 0)
       ) AS autonym

SELECT count(ids) as value, DATE_FORMAT( concat( date( c.Dates ), ' ', HOUR ( c.Dates ), ':', floor( MINUTE (c.Dates ) / 10 ) * 10 ), '%Y-%m-%d ' ) AS time, Names FROM (
SELECT date_format( e.eDate, '%Y-%m-%d') as Dates, e.eName as Names, e.eId as ids FROM enter AS e WHERE e.aId=#{aId}
) as c GROUP BY DATE_FORMAT(time, '%Y-%m-%d'),Names ORDER BY time;

SELECT count(ids) as value, DATE_FORMAT( concat( date( c.Dates ), ' ', HOUR ( c.Dates ), ':', floor( MINUTE (c.Dates ) / 10 ) * 10 ), '%Y-%m-%d %H:%i:%s' ) AS time, Names FROM (
SELECT date_format(t.teDate, '%Y-%m-%d %H:%i:%s') as Dates, date_format(t.teDate, '%Y-%m-%d') as Names, t.teId as ids FROM tloe AS t WHERE t.teaId=#{aId}
) as c GROUP BY DATE_FORMAT(time, '%Y-%m-%d %H:%i:%s' ),Names ORDER BY time;

SELECT count(ids) as value, DATE_FORMAT( concat( date( c.Dates ), ' ', HOUR ( c.Dates ), ':', floor( MINUTE (c.Dates ) / 10 ) * 10 ), '%Y-%m-%d %H:%i:%s' ) AS time, Names FROM (
SELECT date_format( e.eDate, '%Y-%m-%d %H:%i:%s') as Dates, date_format( e.eDate, '%Y-%m-%d') as Names, e.eId as ids FROM enter AS e WHERE e.aId=#{aId}
) as c GROUP BY DATE_FORMAT(time, '%Y-%m-%d %H:%i:%s' ),Names ORDER BY time;
